package com.runwaysdk.dataaccess.cache;

import com.runwaysdk.ServerExceptionMessageLocalizer;
import com.runwaysdk.dataaccess.DataAccessException;
import com.runwaysdk.dataaccess.MetadataDAOIF;


/**
 * Thrown when an object is requested that does not exist (the id can not be
 * found).
 * 
 * @author Eric Grunzke
 */
public class DataNotFoundException extends DataAccessException
{
  /**
   * Generated by Eclipse
   */
  private static final long serialVersionUID = -37355946353129382L;
  
  /**
   * The metadata describing the type of object that was requested
   */
  private MetadataDAOIF metadata;

  /**
   * Constructs a new DataNotFoundException with the specified developer
   * message.
   * 
   * @param devMessage
   *          The non-localized developer error message. Contains specific data
   *          access layer information useful for application debugging. The
   *          developer message is saved for later retrieval by the
   *          {@link #getMessage()} method.
   * @param metadata
   *          The metadata describing the type of object that was requested
   */
  public DataNotFoundException(String devMessage, MetadataDAOIF metadata)
  {
    super(devMessage);
    this.metadata = metadata;
  }

  /**
   * Constructs a new DataNotFoundException with the specified developer message
   * and cause.
   * <p>
   * Note that the detail message associated with <code>cause</code> is <i>not</i>
   * automatically incorporated in this DataNotFoundException's detail message.
   * 
   * @param devMessage
   *          The non-localized developer error message. Contains specific data
   *          access layer information useful for application debugging. The
   *          developer message is saved for later retrieval by the
   *          {@link #getMessage()} method.
   * @param metadata
   *          The metadata describing the type of object that was requested
   * @param cause
   *          the cause (which is saved for later retrieval by the
   *          {@link #getCause()} method). (A <tt>null</tt> value is
   *          permitted, and indicates that the cause is nonexistent or
   *          unknown.)
   */
  public DataNotFoundException(String devMessage, MetadataDAOIF metadata, Throwable cause)
  {
    super(devMessage, cause);
    this.metadata = metadata;
  }

  /**
   * Constructs a new DataNotFoundException with the specified cause and a
   * developer message taken from the cause. This constructor is useful if the
   * DataNotFoundException is a wrapper for another throwable.
   * 
   * @param metadata
   *          The metadata describing the type of object that was requested
   * @param cause
   *          the cause (which is saved for later retrieval by the
   *          {@link #getCause()} method). (A <tt>null</tt> value is
   *          permitted, and indicates that the cause is nonexistent or
   *          unknown.)
   */
  public DataNotFoundException(MetadataDAOIF metadata, Throwable cause)
  {
    super(cause);
    this.metadata = metadata;
  }

  /**
   * Fetches the localized message template and plugs in the correct parameters
   * to set the business error message.
   * 
   */
  public String getLocalizedMessage()
  {
    return ServerExceptionMessageLocalizer.dataNotFoundException(this.getLocale(), this.metadata);
  }
}
